import React, { useContext, useState, useEffect } from 'react';
import '../Styles/Categories.css';
import { Form, Modal, Button, Alert } from "react-bootstrap"
import MasterContext from '../contexts/MasterContext';
import { Link, useNavigate } from 'react-router-dom'
import propTypes from 'prop-types';

const CategoryType = {
    income: 'income',
    expenditure: 'expenditure'
}




const CategoryTransactions = ({ userId }) => {
    const [showCategoryModal, setShowCategoryModal] = useState(false);
    const [showTransactionModal, setShowTransactionModal] = useState(false);
    const [kategorijaID, setKategorijaID] = useState();
    const [Categoryname, setCategoryName] = useState('');
    const [limit, setLimit] = useState('');
    const [type, setType] = useState(true);
    const [Transactionname, setTransactionName] = useState('');
    const [value, setValue] = useState('');
    const { CategoryList, setCategoryList, addCategory, TransactionList, setTransactionList, addExpenditure, addIncome } = useContext(MasterContext);
    const [transactionType, setTransactionType] = useState('')
    const navigate = useNavigate();
    const UserID = sessionStorage.getItem('userID')
    CategoryTransactions.propTypes = {
        Income: propTypes.shape({
            id: propTypes.number.isRequired,
            name: propTypes.string.isRequired,
            amount: propTypes.number.isRequired, 
            date: propTypes.string.isRequired,
            CategoryID: propTypes.number.isRequired
            }),
        Expenditure: propTypes.shape({
            id: propTypes.number.isRequired,
            name: propTypes.string.isRequired,
            amount: propTypes.number.isRequired, 
            CategoryID: propTypes.number.isRequired
        }),
          Category: propTypes.shape({
            id: propTypes.number.isRequired,
            name: propTypes.string.isRequired,
            spendingLimit: propTypes.number.isRequired,
            categoryType: propTypes.oneOf(Object.values(CategoryType)).isRequired
          })
    }
    


    useEffect(() => {
        console.log("Kategorija ID in TransactionType", kategorijaID);
    }, [kategorijaID]);



  return (
    <div className="add-category-container">
      <button className='btn1' onClick={() => setShowCategoryModal(true)}>Add Category</button>
        <div className="modal">
            <Modal show={showCategoryModal} onHide={() =>setShowCategoryModal(false)}>
                <Modal.Header closeButton>
                    <Modal.Title>Add Category</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                    <Form.Group>
                        <Form.Label>Category Name</Form.Label>
                        <Form.Control 
                        type="text" 
                        placeholder="Enter category name" 
                        value={Categoryname} 
                        onChange={(e) => setCategoryName(e.target.value)} 
                        />
                    </Form.Group>
                    <Form.Group>
                        <Form.Label>Limit</Form.Label>
                        <Form.Control 
                        type="number" 
                        placeholder="Enter limit" 
                        value={limit} 
                        onChange={(e) => setLimit(e.target.value)} 
                        />
                    </Form.Group>
                    <Form.Group>
                        <Form.Label>Type</Form.Label>
                        <Form.Control 
                        as="select" 
                        value={type} 
                        onChange={(e) => setType(e.target.value === 'true')}
                        >
                        <option value={true}>Income</option>
                        <option value={false}>Expenditure</option>
                        </Form.Control>
                    </Form.Group>
                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={() =>setShowCategoryModal(false)}>
                    Close
                    </Button>
                    <Button variant="primary" onClick={() => {

                      addCategory(Categoryname, limit, type);
                     }}>
                    Add
                    </Button>
                </Modal.Footer>
                </Modal>
        </div>
        <div className="modal">
        <Modal show={showTransactionModal} onHide={()=>showTransactionModal(false)}>
            <Modal.Header closeButton>
                <Modal.Title>Add Transaction</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                <Form.Group>
                    <Form.Label>Transaction Name</Form.Label>
                    <Form.Control 
                    type="text" 
                    placeholder="Enter transaction name" 
                    value={Transactionname} 
                    onChange={(e) => setTransactionName(e.target.value)} 
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label>Value</Form.Label>
                    <Form.Control 
                    type="number" 
                    placeholder="Enter value" 
                    value={value} 
                    onChange={(e) => setValue(e.target.value)} 
                    />
                </Form.Group>
                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="btn2" onClick={() =>setShowTransactionModal(false)}>
                Close
                </Button>
                <button 
                    variant="btn1" 
                    onClick={() => {
                        //if(transactionType === 'true' ){
                        addIncome(Transactionname, currentKategorijaID, value)
                        //if(transactionType === 'false'){
                       // addExpenditure(Transactionname, kategorijaID, value)
                        //}
                        
                        
                        }}>
                                Add Income
                  </button>
            </Modal.Footer>
            </Modal>
        </div>
        {(CategoryList && Array.isArray(CategoryList)) && CategoryList.map(category => (
        <div key={category.id} className="category-box">
            {console.log("ID Kategorije", category.id)}
          <h3>Category Name: {category.name}</h3>
          <h3>Limit: {category.limita} EUR</h3>
          {category.transactions && category.transactions.length > 0 ? (
                        category.transactions.map(transaction => (
                            <div key={transaction.id}>
                                <p>Name: {transaction.name}</p>
                                <p>Value: {transaction.value}</p>
                                <p>Category Name: {category.name}</p>
                            </div>
                        ))
                    ) : (
                        <p>No transactions for this category.</p>
                    )}
         
        <button className='btn1' onClick={() => {
            const currentKategorijaID = category.id;
            const currentTransactionType = category.type;
            console.log("Kategorija ID in TransactionType: " + currentKategorijaID, currentTransactionType);
            
            setShowTransactionModal(true);
        }}>
            Add Transaction
        </button>
        </div>
      ))}
    </div>
  );
};

export default CategoryTransactions;
