import React, { useState, useContext } from 'react';
import LoginContext from '../contexts/LoginContext';
import { Button, Form, Modal } from 'react-bootstrap';

function LogInModal() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const { authenticateUser, registerUser } = useContext(LoginContext);
    const [showLoginModal, setShowLoginModal] = useState(false)
    const [showRegisterModal, setShowRegisterModal] = useState(false);

    const handleUsernameChange = event => {
        setUsername(event.target.value);
    }

    const handlePasswordChange = event => {
        setPassword(event.target.value);
    }

    const handleSubmit = event => {
        event.preventDefault();
        authenticateUser(username, password);
        setUsername('');
        setPassword('');
    }

    const handleRegister = event => {
        event.preventDefault();
        registerUser(username, password);
        setUsername('');
        setPassword('');
        setShowRegisterModal(false); // Hide the modal after registering
    }

    return (
        <>
        <button className="Navbtn" onClick={() => setShowLoginModal(true)}>Login</button>
        <Modal show={showLoginModal} onHide={() => setShowLoginModal(false)}>
            <Form onSubmit={handleSubmit}>
                {/* Other components in the login modal */}
                <div className="d-flex justify-content-left">
                    <Button variant="primary" onClick={() => setShowRegisterModal(true)}>
                    Register
                    </Button>
                </div>
                <div className="d-flex justify-content-end">
                    <Button variant="primary" type="submit">
                    Login
                    </Button>
                </div>
            </Form>
        </Modal>

        {/* Register Modal */}
        <Modal show={showRegisterModal} onHide={() => setShowRegisterModal(false)}>
            <Form onSubmit={handleRegister}>
                <Modal.Header closeButton>
                    <Modal.Title>Register</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    {/* User registration form similar to login form */}
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="primary" type="submit">
                        Submit
                    </Button>
                </Modal.Footer>
            </Form>
        </Modal>
        </>
    );
}

export default LogInModal;
