import React , { useContext} from "react"
import TransactionsContext from "../contexts/TransactionsContext"
import PropTypes from 'prop-types';
import { propTypes } from "react-bootstrap/esm/Image";
import styles from '../App.css'

const Cashless = {
    Cash: 'Cash',
    Card: 'Card'
}
const CategoryType = {
    income: 'income',
    expenditure: 'expenditure'
}

const CategoryTransactions = PropTypes.shape({
    id: PropTypes.number.isRequired,
    description: PropTypes.string.isRequired,
    amount: PropTypes.number.isRequired,
    date: PropTypes.string.isRequired,
    beneficiary: PropTypes.string.isRequired,
    transactionType: PropTypes.oneOf(Object.values(CategoryType)).isRequired,
    cashCredit: PropTypes.oneOf(Object.values(Cashless)).isRequired,
    categoryID: PropTypes.number.isRequired
});

const formatter = new Intl.NumberFormat('USD', {
    style: 'currency',
    currency: 'USD',
  },
  'EUR', {
    style:'currency',
    currency: 'EUR',

  }
  
  );




  class AlltransactionsTable extends React.Component {
    static propTypes = {
        CategoryTransactions: PropTypes.arrayOf(TransactionPropType).isRequired
    };

    render() {
        const { CategoryTransactions } = this.props;

        return (
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Description</th>
                        <th>Amount</th>
                        <th>Date</th>
                        <th>Beneficiary</th>
                        <th>Transaction Type</th>
                        <th>Cash or Credit</th>
                        <th>Category ID</th>
                    </tr>
                </thead>
                <tbody>
                    {CategoryTransactions.map(transaction => (
                        <tr key={transaction.id}>
                            <td>{transaction.id}</td>
                            <td>{transaction.description}</td>
                            <td>{transaction.amount}</td>
                            <td>{transaction.date}</td>
                            <td>{transaction.beneficiary}</td>
                            <td>{transaction.transactionType}</td>
                            <td>{transaction.cashCredit}</td>
                            <td>{transaction.categoryID}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        );
    }
}

export default AlltransactionsTable;
