import React, { useContext } from 'react';
import { Link, Route, Routes } from 'react-router-dom';
import { LoginContext, LoginProvider } from '../src/components/LoginProvider';
import LogInModal from '../src/components/LogInModal'
import Home from './pages/Home';
import Overview from './pages/Overview';
import AllTransactions from './pages/AllTransactions';
import Budget from './pages/Budget';
import { CategoryProvider } from './components/CategoryProvider';

function App() {
  const progress = 50; 


  const headerStyle = {
    backgroundColor: 'palegreen',
    height: '50px',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '0 30px',
    boxShadow: '0 2px 4px 0 rgba(0,0,0,0.10)',
    marginBottom: '20px',
  };

  const navItemStyle = {
    listStyleType: 'none',
    margin: '0 10px',
  };

  const linkStyle = {
    textDecoration: 'none',
    color: 'black',
  };

  const buttonStyle = {
    backgroundColor: 'transparent',
    border: 'none',
    cursor: 'pointer',
    color: 'black',
    padding: '10px',
  };

  const footerStyle = {
    backgroundColor: 'palegreen',
    height: '50px',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    marginTop: '20px',
    boxShadow: '0 -2px 4px 0 rgba(0,0,0,0.10)',
  };


  return (
      <>
      <header style={headerStyle}>
      {
        <ul className="nav nav-pills" style={{ display: 'flex' }}>
          <li style={navItemStyle}>
            <button style={buttonStyle}><Link to='/pages/Home' style={linkStyle}>Home</Link></button>
          </li>
          <li style={navItemStyle}>
            <button style={buttonStyle}><Link to='/pages/Overview' style={linkStyle}>Overview</Link></button>
          </li>
          <li style={navItemStyle}>
            <button style={buttonStyle}><Link to='/pages/Budget' style={linkStyle}>Budgeting</Link></button>
          </li>
          <li style={navItemStyle}>
            <button style={buttonStyle}><Link to='/pages/AllTransactions' style={linkStyle}>Transactions</Link></button>
          </li>
          <li style={navItemStyle}>
          </li>
          <li style={navItemStyle}>
            <label>Budget: XYZ$ / XYZ$</label>
            <progress value={progress} max={100}></progress>
          </li>
        </ul>
      }
      </header>
      <CategoryProvider>
      <Routes>
        <Route path="/pages/Home" element={<Home />} />
        <Route path="/pages/Overview" element={<Overview />} />
        <Route path="/pages/AllTransactions" element={<AllTransactions />} />
        <Route path="/pages/Budget" element={<Budget />} />
      </Routes>
      </CategoryProvider>
      <footer style={footerStyle}>FooterContent</footer>
    </>
  );
}

export default App;
