import React, { useState, useEffect, useMemo, useContext } from "react"
import DataTable from 'react-data-table-component';
import TransactionsContext from "../contexts/TransactionsContext"

const AlltransactionsTable = () => {
    const { TransactionList, updateTList } = useContext(TransactionsContext);
    const [sortField, setSortField] = useState(null);
    const [sortDirection, setSortDirection] = useState('asc');

    const columns = useMemo(() => [
        { name: 'ID', selector: 'id' },
        { name: 'Description', selector: 'description' },
        { name: 'Amount', selector: 'amount' },
        { name: 'Date', selector: 'date' },
        { name: 'Beneficiary', selector: 'beneficiary' },
        { name: 'Transaction Type', selector: 'transactionType' },
        { name: 'Cash or Credit', selector: 'cashCredit' },
        { name: 'Category ID', selector: 'categoryID' }
    ], []);

    useEffect(() => {
        const sortTransactions = (list) => {
            return list.sort((a, b) => {
                if (a[sortField] < b[sortField]) {
                    return sortDirection === 'asc' ? -1 : 1;
                }
                if (a[sortField] > b[sortField]) {
                    return sortDirection === 'asc' ? 1 : -1;
                }
                return 0;
            });
        };

        if (sortField !== null) {
            updateTList(sortTransactions(TransactionList));
        }
    }, [sortField, sortDirection]);

    const handleSort = (column, direction) => {
        setSortField(column.selector);
        setSortDirection(direction);
    };

    return (
        <div className="table-container">
            <div className="table-wrapper">
                <h2 className="table-title">Prihodi</h2>
                <DataTable
                    columns={columns}
                    data={TransactionList}
                    onSort={handleSort}
                    sortServer
                />
            </div>
            <div className="table-wrapper">
                <h2 className="table-title">Odhodi</h2>
                <DataTable
                    columns={columns}
                    data={TransactionList}
                    onSort={handleSort}
                    sortServer
                />
            </div>
        </div>
    );
}

export default AlltransactionsTable;
