import React, { useState } from 'react';
import './CategoryComponent.css'; // Assuming you have a CSS file for styling

const CategoryComponent = ({ userId }) => {
  const [categories, setCategories] = useState([]);
  const [showCategoryModal, setShowCategoryModal] = useState(false);
  const [showTransactionModal, setShowTransactionModal] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState(null);

  const addCategory = (name, limit, type) => {
    // API call to add category
    // For now, we'll mock the API call
    const newCategory = {
      id: categories.length + 1,
      name,
      userID: userId,
      limit,
      type,
      transactions: []
    };
    setCategories([...categories, newCategory]);
    setShowCategoryModal(false);
  };

  const addTransaction = (categoryId, name, value) => {
    // API call to add transaction
    // For now, we'll mock the API call
    const updatedCategories = categories.map(category => {
      if (category.id === categoryId) {
        const newTransaction = {
          id: category.transactions.length + 1,
          name,
          value,
          userId,
          categoryId
        };
        category.transactions.push(newTransaction);
      }
      return category;
    });
    setCategories(updatedCategories);
    setShowTransactionModal(false);
  };

  return (
    <div className="category-container">
      <button onClick={() => setShowCategoryModal(true)}>Add Category</button>
      {showCategoryModal && (
        <div className="modal">
           <Modal show={showAddCategoryModal} onHide={() => setshowAddCategoryModal(false)}>
                    <Form onSubmit={handleSubmit}>
                        <Modal.Header closeButton>
                        <Modal.Title>Add Category</Modal.Title>
                        </Modal.Header>
                        <Modal.Body>
                        <Form.Group className="mb-3" controlId="description">
                            <Form.Label>Category name:</Form.Label>
                            <Form.Control ref={NameRef} type="text" required />
                        </Form.Group>
                        <Form.Group className="mb-3" controlId="amount">
                            <Form.Label>Spending Limit:</Form.Label>
                            <Form.Control
                            ref={LimitRef}
                            type="number"
                            required
                            min={0}
                            step={0.01}
                            />
                        </Form.Group>
                        <Form.Group className="mb-3" controlId="amount">
                        <Form.Select defaultValue={SelectedType} ref={selectedRef}>
                            <option value={CategoryType.income}>Income</option>
                            <option value={CategoryType.expenditure}>Expenditure</option>
                        </Form.Select>
                        </Form.Group>
                        <div className="d-flex justify-content-end">
                            <button className="Overviewbtn" type="submit">
                            Add
                            </button>
                        </div>
                        </Modal.Body>
                    </Form>
                </Modal>
        </div>
      )}
      {showTransactionModal && (
        <div className="modal">
           <Modal show={showAddTransactionModal} onHide={() => setShowAddTransactionModal(false)}>
                <Form>
                    <Modal.Header closeButton>
                    <Modal.Title>New Transaction</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                    <Form.Group className="mb-3" controlId="description">
                        <Form.Label>Transaction Description:</Form.Label>
                        <Form.Control type="text" required ref={DescRep}/>
                    </Form.Group>
                    <Form.Group className="mb-3" controlId="amount">
                        <Form.Label>Amount:</Form.Label>
                        <Form.Control
                        ref={AmRef}
                        type="number"
                        required
                        min={0}
                        step={0.01}
                        />
                    </Form.Group>
                    <Form.Group className="mb-3" controlId="Beneficiary">
                        <Form.Label>Beneficiary:</Form.Label>
                        <Form.Control type="text" required ref={BenRef}/>
                    </Form.Group>
                    <div className="d-flex justify-content-end">
                    <button className="Overviewbtn" type="button" onClick={AddTransaction}>
                        Add Transaction
                        </button>
                    </div>
                    </Modal.Body>
                </Form>
            </Modal>
        </div>
      )}
      {categories.map(category => (
        <div key={category.id} className="category-box">
          <h3>{category.name}</h3>
          {category.transactions.map(transaction => (
            <div key={transaction.id} className="transaction">
              <span>{transaction.name}</span>
              <span>{transaction.value}</span>
            </div>
          ))}
          <button onClick={() => {
            setSelectedCategory(category.id);
            setShowTransactionModal(true);
          }}>
            Add Transaction
          </button>
        </div>
      ))}
    </div>
  );
};

export default CategoryComponent;
