import { useContext, useState } from "react"
import TransactionsContext from "../contexts/TransactionsContext"
import PropTypes from 'prop-types';
import CategoryContext from "../contexts/CategoryContext";

const CategoryType = {
    income: 'income',
    expenditure: 'expenditure'
}

PropTypes.AlltransactionsTable = {
    Income: PropTypes.shape({
        id: PropTypes.number.isRequired,
        name: PropTypes.string.isRequired,
        amount: PropTypes.number.isRequired, 
        date: PropTypes.string.isRequired,
        CategoryID: PropTypes.number.isRequired
        }),
    Expenditure: PropTypes.shape({
        id: PropTypes.number.isRequired,
        name: PropTypes.string.isRequired,
        amount: PropTypes.number.isRequired, 
        CategoryID: PropTypes.number.isRequired
    }),
      Category: PropTypes.shape({
        id: PropTypes.number.isRequired,
        name: PropTypes.string.isRequired,
        spendingLimit: PropTypes.number.isRequired,
        categoryType: PropTypes.oneOf(Object.values(CategoryType)).isRequired
      })
}


export default function AlltransactionsTable(){
    const { IncomeList, ExpenditureList, updateEList, updateIList } = useContext(TransactionsContext) 
    const { CategoryList } = useContext(CategoryContext)
    const PCategoryName = useState("");
    const PCategoryNumber = useState();
    const CCategory = useState();
    const OCategoryName = useState("");
    const OCategoryNumber = useState();
    const OCCategory = useState();

    const findCategorybyID = (id) => {
        return CategoryList.find(Category => Category.id === id)
    }
    return(
        <div>
            <h4>Incomes</h4>
            <table>
                <thead>
                    <tr>
                        <th>Category</th>
                        <th>Amount</th>
                        <th>Description</th>
                    </tr>
                    {IncomeList.map(Income => (
                        <tr>
                            {(() => {
                                PCategoryNumber = Income.CategoryID
                                CCategory = findCategorybyID(PCategoryNumber)
                                PCategoryName = CCategory.name
                                return null

                            })}
                            <th>{PCategoryName}</th>
                            <th>{Income.amount}</th>
                            <th>{Income.name}</th>
                            <th>{Income.date}</th>

                        </tr>
                    ))}
                </thead>
                <tbody>
                </tbody>
            </table>
            <h4>Expenditures</h4>
            <table className="ExpenditureTable">
                <thead>
                    <tr>
                        <th>Category</th>
                        <th>Amount</th>
                        <th>Description</th>
                    </tr>
                    {ExpenditureList.map(Expenditure => (
                        <tr>
                             {(() => {
                                OCategoryNumber = Expenditure.CategoryID
                                OCCategory = findCategorybyID(OCategoryNumber)
                                OCategoryName = CCategory.name
                                return null

                            })}
                            <th>{OCategoryName}</th>
                            <th>{Expenditure.amount}</th>
                            <th>{Expenditure.name}</th>
                            <th>{Expenditure.date}</th>

                        </tr>
                    ))}
                </thead>
                <tbody>
                </tbody>
            </table>
        </div>
    )



}
