import React, { useState, useContext } from 'react';
import { Button, Form, Modal } from 'react-bootstrap';
import { LoginProvider, LoginContext } from './LoginProvider';

function LogInModal() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [email, setEmail] = useState('');
    const {authenticateUser, registerUser, setIsLoggedIn } = useContext(LoginContext);
    const [showLoginModal, setShowLoginModal] = useState(false)
    const [showRegisterModal, setShowRegisterModal] = useState(false);

    const handleUsernameChange = event => {
        setUsername(event.target.value);
    }

    const handlePasswordChange = event => {
        setPassword(event.target.value);
    }

    const handleEmailChange = event => {
        setEmail(event.target.value)
    }

    const handleSubmit = event => {
        event.preventDefault();
        authenticateUser(username, password, email);
        setUsername('');
        setPassword('');
        setEmail('');
    }

    const handleRegister = event => {
        event.preventDefault();
        registerUser(username, password, email);
        setUsername('');
        setPassword('');
        setEmail('');
        setShowRegisterModal(false);
    }

    const buttonStyle = {
        backgroundColor: 'transparent',
        border: 'none',
        cursor: 'pointer',
        color: 'black',
        padding: '10px',
      };

    return (
        <>
        <button button style={buttonStyle} onClick={() => setShowLoginModal(true)}>Login</button>
        <Modal show={showLoginModal} onHide={() => setShowLoginModal(false)}>
            <Form onSubmit={handleSubmit}>
                <Modal.Header closeButton>
                <Modal.Title>Log In</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                <Form.Group className="mb-3" controlId="Username">
                    <Form.Label>Username:</Form.Label>
                    <Form.Control type="text" required onChange={handleUsernameChange} />
                </Form.Group>
                <Form.Group className="mb-3" controlId="Paswword">
                    <Form.Label>Password:</Form.Label>
                    <Form.Control
                      type="password" required onChange={handlePasswordChange}
                    />
                </Form.Group>
                <div className="d-flex justify-content-left">
                    <Button variant="primary" onClick={() => setShowRegisterModal(true)}>
                    Register
                    </Button>
                </div>
                <div className="d-flex justify-content-">
                    <Button variant="primary" type="submit">
                    Login
                    </Button>
                </div>
                </Modal.Body>
            </Form>
        </Modal>
        </>
    );
}

export default LogInModal;
