import React, { useState, useContext } from 'react';;
import { Button, Form, Modal } from 'react-bootstrap';

function LogInModal() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const { authenticateUser, registerUser } = useContext(LoginContext);
    const [showLoginModal, setShowLoginModal] = useState(false)

    const handleUsernameChange = event => {
        setUsername(event.target.value);
    }

    const handlePasswordChange = event => {
        setPassword(event.target.value);
    }

    const handleSubmit = event => {
        event.preventDefault();
        authenticateUser(username, password);
        setUsername('');
        setPassword('');
    }

    const handleRegister = event => {
        event.preventDefault();
        registerUser(username, password);
        setUsername('');
        setPassword('');
    }

    return (
        <>
        <button className="Navbtn" onClick={() => setShowLoginModal(true)}>Login</button>
        <Modal show={showLoginModal} onHide={() => setShowLoginModal(false)}>
            <Form onSubmit={handleSubmit}>
                <Modal.Header closeButton>
                <Modal.Title>Log In</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                <Form.Group className="mb-3" controlId="Username">
                    <Form.Label>Username:</Form.Label>
                    <Form.Control type="text" required onChange={handleUsernameChange} />
                </Form.Group>
                <Form.Group className="mb-3" controlId="Paswword">
                    <Form.Label>Password:</Form.Label>
                    <Form.Control
                      type="password" required onChange={handlePasswordChange}
                    />
                </Form.Group>
                <div className="d-flex justify-content-left">
                    <Button variant="primary" onClick={handleRegister}>
                    Register
                    </Button>
                </div>
                <div className="d-flex justify-content-end">
                    <Button variant="primary" type="submit">
                    Login
                    </Button>
                </div>
                </Modal.Body>
            </Form>
        </Modal>
        </>
    );
}

export default LogInModal;
