import React, { createContext, useState } from "react";
import axios from 'axios';

const LoginContext = createContext();

const LoginProvider = ({ children }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [userID, setUserID] = useState();
  const [email, setEmail] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const authenticateUser = async (username, password) => {
    try {
      const response = await axios.post('http://localhost:8080/api/v1/user', { username, password });
      if (response.status === 200) {
        setUserID(response.data.id);
        setUsername(username);
        setPassword(password);
      }
    } catch (error) {
      console.error(error);
    }
  };

  const registerUser = async (username, password, email) => {
    try {
      const response = await axios.post('http://localhost:8080/api/v1/user', { username, password, email });
      if (response.status === 200) {
        setUsername(username);
        setPassword(password);
        setEmail(email);
      }
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <LoginContext.Provider value={{ username, password, authenticateUser, registerUser, isLoggedIn, setIsLoggedIn }}>
      {children}
    </LoginContext.Provider>
  );
};

export { LoginProvider, LoginContext };
